import React, { useState } from "react";

// کامپوننت Stepper
const Stepper = ({ steps, currentStep }) => {
  return (
    <div style={{ display: "flex", justifyContent: "space-around", margin: "20px 0" }}>
      {steps.map((step, index) => (
        <div key={index} style={{ textAlign: "center" }}>
          <div
            style={{
              width: "30px",
              height: "30px",
              borderRadius: "50%",
              background: currentStep === index ? "blue" : "gray",
              color: "white",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              margin: "0 auto",
            }}
          >
            {index + 1}
          </div>
          <p>{step}</p>
        </div>
      ))}
    </div>
  );
};

// کامپوننت اصلی App
const App = () => {
  const [currentStep, setCurrentStep] = useState(0);

  const steps = ["اطلاعات شخصی", "اطلاعات بانکی", "تسهیلات"];

  // فرم مرحله اول
  const PersonalInfoForm = () => (
    <div>
      <h3>اطلاعات شخصی</h3>
      <div style={{ display: "flex", justifyContent: "space-between", margin: "10px 0" }}>
        <input type="text" placeholder="نام" style={inputStyle} />
        <input type="text" placeholder="نام خانوادگی" style={inputStyle} />
        <input type="text" placeholder="کد ملی" style={inputStyle} />
      </div>
    </div>
  );

  // فرم مرحله دوم
  const BankInfoForm = () => (
    <div>
      <h3>اطلاعات بانکی</h3>
      <div style={{ display: "flex", justifyContent: "space-between", margin: "10px 0" }}>
        <input type="text" placeholder="شماره تماس" style={inputStyle} />
        <input type="date" placeholder="تاریخ تولد" style={inputStyle} />
      </div>
    </div>
  );

  // فرم مرحله سوم
  const LoanInfoForm = () => (
    <div>
      <h3>تسهیلات</h3>
      <div style={{ display: "flex", justifyContent: "space-between", margin: "10px 0" }}>
        <input type="text" placeholder="مبلغ درخواست" style={inputStyle} />
      </div>
    </div>
  );

  // استایل ساده برای ورودی‌ها
  const inputStyle = {
    width: "30%",
    padding: "10px",
    border: "1px solid gray",
    borderRadius: "5px",
  };

  return (
    <div style={{ width: "80%", margin: "0 auto", padding: "20px", textAlign: "center" }}>
      {/* Stepper */}
      <Stepper steps={steps} currentStep={currentStep} />

      {/* فرم‌ها بر اساس مرحله */}
      {currentStep === 0 && <PersonalInfoForm />}
      {currentStep === 1 && <BankInfoForm />}
      {currentStep === 2 && <LoanInfoForm />}

      {/* دکمه‌های کنترلی */}
      <div style={{ marginTop: "20px" }}>
        <button
          onClick={() => setCurrentStep((prev) => Math.max(prev - 1, 0))}
          disabled={currentStep === 0}
          style={{
            padding: "10px 20px",
            background: currentStep === 0 ? "lightgray" : "blue",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: currentStep === 0 ? "not-allowed" : "pointer",
            marginRight: "10px",
          }}
        >
          قبلی
        </button>
        <button
          onClick={() => setCurrentStep((prev) => Math.min(prev + 1, steps.length - 1))}
          disabled={currentStep === steps.length - 1}
          style={{
            padding: "10px 20px",
            background: currentStep === steps.length - 1 ? "lightgray" : "blue",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: currentStep === steps.length - 1 ? "not-allowed" : "pointer",
          }}
        >
          بعدی
        </button>
      </div>
    </div>
  );
};

export default App;
