import React from "react";
import { TextField } from "@mui/material";

const BankInfo = ({ formData, setFormData }) => {
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <h2>اطلاعات بانکی</h2>
      <TextField
        fullWidth
        label="شماره شبا"
        name="iban"
        value={formData.iban}
        onChange={handleChange}
        error={formData.iban.length !== 24 && formData.iban.length > 0}
        helperText={formData.iban.length !== 24 && formData.iban.length > 0 ? "شماره شبا باید ۲۴ رقم باشد" : ""}
        margin="normal"
      />
      <TextField
        fullWidth
        label="شماره حساب"
        name="accountNumber"
        value={formData.accountNumber}
        onChange={handleChange}
        error={formData.accountNumber.length < 8 && formData.accountNumber.length > 0}
        helperText={formData.accountNumber.length < 8 && formData.accountNumber.length > 0 ? "شماره حساب حداقل ۸ رقم باشد" : ""}
        margin="normal"
      />
      <TextField
        fullWidth
        label="گردش سالانه (تومان)"
        name="annualTurnover"
        type="number"
        value={formData.annualTurnover}
        onChange={handleChange}
        error={formData.annualTurnover < 1000000 && formData.annualTurnover > 0}
        helperText={formData.annualTurnover < 1000000 && formData.annualTurnover > 0 ? "حداقل گردش سالانه باید ۱,۰۰۰,۰۰۰ تومان باشد" : ""}
        margin="normal"
      />
    </div>
  );
};

export default BankInfo;
