// src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState('');

    const API_KEY = 'YOUR_API_KEY'; // کلید API خود را اینجا قرار دهید

    const getWeather =  (e) => {
        console.log(e ,"eeeee");
        debugger
        e.preventDefault();
        // setError('');
        
        // try {
            // const response =
              axios.get('https://iran-locations-api.ir/api/v1/fa/cities?state=تهران').then((res)=>{
                console.log(res ,"res");
                
            })
            // setWeatherData(response.data);
        // } catch (err) {
        //     setError('شهر پیدا نشد. لطفا نام شهر را دوباره بررسی کنید.');
        //     setWeatherData(null);
        // }
    };

    return (
        <div className="app">
            <h1>برنامه آب و هوا</h1>
            <form onSubmit={getWeather}>
                <input 
                    type="text" 
                    value={city} 
                    onChange={(e) => setCity(e.target.value)} 
                    placeholder="نام شهر را وارد کنید"
                />
                <button type="submit">جستجو</button>
            </form>
            {error && <p className="error">{error}</p>}
            {weatherData && (
                <div className="weather-info">
                    <h2>{weatherData.name}</h2>
                    <p>دما: {weatherData.main.temp} °C</p>
                    <p>وضعیت: {weatherData.weather[0].description}</p>
                </div>
            )}
        </div>
    );
};

export default App;
