import React, { useState } from "react";
import Stepper from "react-stepper-horizontal";
import { TextField, Button, Box, Typography } from "@mui/material";

const LoanForm = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [formData, setFormData] = useState({
    name: "",
    lastName: "",
    nationalId: "",
    birthDate: "",
    contactNumber: "",
    iban: "",
    accountNumber: "",
    annualBalance: "",
  });
  const [errors, setErrors] = useState({});

  const steps = [
    { title: "اطلاعات شخصی" },
    { title: "اطلاعات بانکی" },
    { title: "تسهیلات" },
  ];

  const handleNext = () => {
    if (validateStep()) {
      if (currentStep < steps.length - 1) setCurrentStep(currentStep + 1);
    }
  };

  const handleBack = () => {
    if (currentStep > 0) setCurrentStep(currentStep - 1);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: "" });
  };

  const validateStep = () => {
    let stepErrors = {};
    if (currentStep === 0) {
      // Validation for step 1
      if (!formData.contactNumber || formData.contactNumber.length !== 11) {
        stepErrors.contactNumber = "شماره موبایل باید 11 رقم باشد.";
      }
      if (!formData.nationalId || formData.nationalId.length !== 10) {
        stepErrors.nationalId = "کد ملی باید 10 رقم باشد.";
      }
    } else if (currentStep === 1) {
      // Validation for step 2
      if (!formData.accountNumber || formData.accountNumber.length < 8 || formData.accountNumber.length > 16) {
        stepErrors.accountNumber = "شماره حساب باید بین 8 تا 16 رقم باشد.";
      }
    }

    setErrors(stepErrors);
    return Object.keys(stepErrors).length === 0;
  };

  const renderFormContent = () => {
    switch (currentStep) {
      case 0:
        return (
          <Box>
            <TextField
              label="نام"
              name="name"
              value={formData.name}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="نام خانوادگی"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="کد ملی"
              name="nationalId"
              value={formData.nationalId}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.nationalId}
              helperText={errors.nationalId}
            />
            <TextField
              label="تاریخ تولد"
              name="birthDate"
              value={formData.birthDate}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="شماره تماس"
              name="contactNumber"
              value={formData.contactNumber}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.contactNumber}
              helperText={errors.contactNumber}
            />
          </Box>
        );
      case 1:
        return (
          <Box>
            <TextField
              label="شماره شبا"
              name="iban"
              value={formData.iban}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="شماره حساب"
              name="accountNumber"
              value={formData.accountNumber}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.accountNumber}
              helperText={errors.accountNumber}
            />
            <TextField
              label="میانگین موجودی ریال سالانه"
              name="annualBalance"
              value={formData.annualBalance}
onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Box>
        );
      case 2:
        return (
          <Box>
            <Typography>اطلاعات تسهیلات</Typography>
            {/* فرم‌های مرحله سوم در اینجا قرار می‌گیرند */}
          </Box>
        );
      default:
        return null;
    }
  };

  return (
    <Box sx={{ width: "50%", margin: "auto", textAlign: "center" }}>
      <Stepper
        steps={steps}
        activeStep={currentStep}
        activeColor="#007bff"
        completeColor="#4caf50"
      />
      {renderFormContent()}
      <Box sx={{ marginTop: 2 }}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleBack}
          disabled={currentStep === 0}
          sx={{ marginRight: 1 }}
        >
          قبلی
        </Button>
        <Button
          variant="contained"
          color="primary"
          onClick={handleNext}
          disabled={currentStep === steps.length - 1}
        >
          بعدی
        </Button>
      </Box>
    </Box>
  );
};

export default LoanForm;
