import React, { useState } from "react";
import { TextField, Button, Box, Typography } from "@mui/material";

const LoanForm = () => {
  const [formData, setFormData] = useState({
    mobileNumber: "",
    nationalCode: "",
  });

  const [errors, setErrors] = useState({
    mobileNumber: "",
    nationalCode: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    // محدود کردن ورودی به اعداد
    if (isNaN(value)) return;

    setFormData({
      ...formData,
      [name]: value,
    });

    // پاک کردن خطا در صورت تصحیح ورودی
    setErrors({
      ...errors,
      [name]: "",
    });
  };

  const validateForm = () => {
    let isValid = true;
    let newErrors = {
      mobileNumber: "",
      nationalCode: "",
    };

    // بررسی شماره موبایل (حداکثر 11 رقم)
    if (formData.mobileNumber.length > 11) {
      isValid = false;
      newErrors.mobileNumber = "شماره موبایل نباید بیشتر از ۱۱ رقم باشد.";
    }

    // بررسی کد ملی (دقیقاً 10 رقم)
    if (formData.nationalCode.length !== 10) {
      isValid = false;
      newErrors.nationalCode = "کد ملی باید دقیقاً ۱۰ رقم باشد.";
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      alert("فرم با موفقیت ارسال شد!");
      // اینجا می‌توانید داده‌ها را به سرور ارسال کنید
      console.log("Form Data: ", formData);
    }
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        maxWidth: 400,
        margin: "auto",
        display: "flex",
        flexDirection: "column",
        gap: 2,
        mt: 5,
      }}
    >
      <Typography variant="h6" align="center">
        فرم اطلاعات
      </Typography>

      {/* فیلد شماره موبایل */}
      <TextField
        label="شماره موبایل"
        name="mobileNumber"
        value={formData.mobileNumber}
        onChange={handleChange}
        error={Boolean(errors.mobileNumber)}
        helperText={errors.mobileNumber}
        inputProps={{ maxLength: 11 }} // جلوگیری از وارد کردن بیش از 11 کاراکتر
        fullWidth
      />

      {/* فیلد کد ملی */}
      <TextField
        label="کد ملی"
        name="nationalCode"
        value={formData.nationalCode}
        onChange={handleChange}
        error={Boolean(errors.nationalCode)}
        helperText={errors.nationalCode}
        inputProps={{ maxLength: 10 }} // جلوگیری از وارد کردن بیش از 10 کاراکتر
        fullWidth
      />

      {/* دکمه ارسال */}
      <Button variant="contained" type="submit" fullWidth>
        ارسال
      </Button>
    </Box>
  );
};

export default LoanForm;
