import React, { useState } from 'react';
import Stepper from 'react-stepper-horizontal';
import { TextField, Button, Box } from '@mui/material';

const LoanForm = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [formData, setFormData] = useState({
    name: '',
    lastName: '',
    nationalId: '',
    birthDate: '',
    contactNumber: '',
    iban: '',
    accountNumber: '',
    annualBalance: '',
  });

  const steps = [
    { title: 'اطلاعات شخصی' },
    { title: 'اطلاعات بانکی' },
    { title: 'تسهیلات' },
  ];

  const handleNext = () => {
    if (currentStep < steps.length - 1) setCurrentStep(currentStep + 1);
  };

  const handleBack = () => {
    if (currentStep > 0) setCurrentStep(currentStep - 1);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const renderFormContent = () => {
    switch (currentStep) {
      case 0:
        return (
          <Box>
            <TextField
              label="نام"
              name="name"
              value={formData.name}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="نام خانوادگی"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="کد ملی"
              name="nationalId"
              value={formData.nationalId}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="تاریخ تولد"
              name="birthDate"
              value={formData.birthDate}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="شماره تماس"
              name="contactNumber"
              value={formData.contactNumber}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Box>
        );
      case 1:
        return (
          <Box>
            <TextField
              label="شماره شبا"
              name="iban"
              value={formData.iban}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="شماره حساب"
              name="accountNumber"
              value={formData.accountNumber}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="میانگین موجودی ریال سالانه"
              name="annualBalance"
              value={formData.annualBalance}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Box>
        );
      case 2:
        return (
          <Box>
            <h3>اطلاعات تسهیلات</h3>
            {/* فرم‌های مرحله سوم در این بخش اضافه می‌شود */}
          </Box>
        );
      default:
        return null;
    }
  };

  return (
    <Box sx={{ width: '50%', margin: 'auto', textAlign: 'center' }}>
      <Stepper
        steps={steps}
        activeStep={currentStep}
        activeColor="#007bff"
        completeColor="#4caf50"
      />
      {renderFormContent()}
      <Box sx={{ marginTop: 2 }}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleBack}
          disabled={currentStep === 0}
          sx={{ marginRight: 1 }}
        >
          قبلی
        </Button>
        <Button
          variant="contained"
          color="primary"
          onClick={handleNext}
          disabled={currentStep === steps.length - 1}
        >
          بعدی
        </Button>
      </Box>
    </Box>
  );
};

export default LoanForm;
