// src/App.js
import React, { useState, useEffect } from 'react';
import NoteList from './NoteList';
<style>
   .direction=rtl

</style>
const App = () => {
    const [notes, setNotes] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [editId, setEditId] = useState(null);

    useEffect(() => {
        const savedNotes = JSON.parse(localStorage.getItem('notes')) || [];
        setNotes(savedNotes);
    }, []);

    useEffect(() => {
        localStorage.setItem('notes', JSON.stringify(notes));
    }, [notes]);

    const handleAddNote = () => {
        if (inputValue.trim() === '') return;

        if (editId) {
            setNotes(notes.map(note => note.id === editId ? { ...note, text: inputValue } : note));
            setEditId(null);
        } else {
            const newNote = { id: Date.now(), text: inputValue };
            setNotes([...notes, newNote]);
        }
        
        setInputValue('');
    };

    const handleDeleteNote = (id) => {
        setNotes(notes.filter(note => note.id !== id));
    };

    const handleEditNote = (id) => {
        const noteToEdit = notes.find(note => note.id === id);
        setInputValue(noteToEdit.text);
        setEditId(id);
    };

    return (
        <div className="app">
            <h1>یادداشت‌برداری</h1>
            <input 
                type="text" 
                value={inputValue} 
                onChange={(e) => setInputValue(e.target.value)} 
                placeholder="یادداشت جدید"
                style={{direction:"rsl", textAlign:center}}
            />
            <button onClick={handleAddNote}>{editId ? 'ویرایش' : 'افزودن'}</button>
            <NoteList notes={notes} onDelete={handleDeleteNote} onEdit={handleEditNote} />
        </div>
    );
};



export default App;
