import React, { useState } from 'react';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');

  const handleInputChange = (e) => {
    setTask(e.target.value);
  };

  const handleAddTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask('');
    }
  };

  const handleDeleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  return (
    <div style={{ backgroundColor:'lightyellow' ,maxWidth: '400px', margin: '0 auto', textAlign: 'center' }}>
      <h1>Todo List</h1>
      <input
        type="text"
        value={task}
        onChange={handleInputChange}
        placeholder="üìÑ"
        style={{ width: '70%', padding: '10px', marginRight: '10px' }}
      />
      <button onClick={handleAddTask} style={{ padding: '10px' }}>‚ûï</button>
      <ul style={{ listStyleType: 'none', padding: '0' }}>
        {tasks.map((task, index) => (
          <li key={index} style={{  display: 'flex', justifyContent: 'space-between', alignItems: 'center', margin: '10px 0' }}>
            <span>{task}</span>
            <button onClick={() => handleDeleteTask(index)} style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '5px' }}>‚úñÔ∏è</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
