import React, { useState } from "react";
import "./app.css";

import {
  Box,
  Stepper,
  Step,
  StepLabel,
  Button,
  TextField,
  Typography,
} from "@mui/material";
import "./app.css";
import TextInput from "./component/inputs/TextInput";

const steps = ["اطلاعات شخصی", "اطلاعات بانکی", "تسهیلات"];

const LoanStepper = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    nationalCode: "",
    birthDate: "",
    contactNumber: "",
    iban: "",
    accountNumber: "",
    annualBalance: "",
  });

  const [errors, setErrors] = useState({
    firstName: "",
    nationalCode: "",
    contactNumber: "",
    iban: "",
    accountNumber: "",
  });

  const handleNext = () => {
    if (validateFields()) {
      setActiveStep((prevActiveStep) => prevActiveStep + 1);
    }
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    validateField(name, value);
  };

  const validateField = (name, value) => {
    let error = "";

    switch (name) {
      case "contactNumber":
        if (!/^\d{11}$/.test(value)) {
          error = "شماره تماس باید 11 رقم باشد.";
        }
        break;
      case "nationalCode":
        if (!/^\d{10}$/.test(value)) {
          error = "کد ملی باید 10 رقم باشد.";
        }
        break;
      case "iban":
        if (!/^\d{16}$/.test(value)) {
          error = "شماره شبا باید 16 رقم باشد.";
        }
        break;
      case "accountNumber":
        if (!/^\d{12}$/.test(value)) {
          error = "شماره حساب باید 12 رقم باشد.";
        }
        break;
      default:
        break;
    }

    setErrors((prevErrors) => ({ ...prevErrors, [name]: error }));
  };

  const validateFields = () => {
    let isValid = true;

    Object.keys(errors).forEach((field) => {
      validateField(field, formData[field]);
      if (errors[field]) {
        isValid = false;
      }
    });

    return isValid;
  };

  const renderStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <Box className="form-container">
         <TextInput label="نام" />
            <TextField
              label="نام خانوادگی"/>
            <TextField
              label= " کد ملی"/>
            <TextField
              label="تاریخ تولد"
              name="birthDate"
              value={formData.birthDate}
              onChange={handleChange}
              fullWidth
              margin="normal"
              className="input-field"
            />
            <TextField
              label="شماره تماس"
              name="contactNumber"
              value={formData.contactNumber}
              onChange={handleChange}
              error={Boolean(errors.contactNumber)}
              helperText={errors.contactNumber}
              fullWidth
              margin="normal"
              className="input-field"
            />
          </Box>
        );
      case 1:
        return (
          <Box className="form-container">
            <TextField
            label="شماره حساب"
            name="accountNumber"
            value={formData.accountNumber}
            onChange={handleChange}
            error={Boolean(errors.accountNumber)}
            helperText={errors.accountNumber}
            margin="normal"
            className="input-field"
            />
            <TextField
            label="شماره شبا"
            name="iban"
            value={formData.iban}
            onChange={handleChange}
            error={Boolean(errors.iban)}
            helperText={errors.iban}
            fullWidth
            margin="normal"
            className="input-field"
            />
            <TextField
              label="موجودی سالانه (ریال)"
              name="annualBalance"
              value={formData.annualBalance}
              onChange={handleChange}
              fullWidth
              margin="normal"
              className="input-field"
            />
          </Box>
        );
      case 2:
        return <Typography variant="h1">تایید شد</Typography>;
      default:
        return "گام نامعتبر";
    }
  };

  return (
    <Box className="loan-stepper-container">
      <Stepper activeStep={activeStep} className="stepper-box">
        {steps.map((label, index) => (
          <Step key={index}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <Box sx={{ mt: 3 }}>
        {renderStepContent(activeStep)}
        <Box className="button-group">
          <Button
            disabled={activeStep === 0}
            onClick={handleBack}
            variant="contained"
            color="secondary"
          >
            قبلی
          </Button>
          {activeStep === steps.length - 1 ? (
            <Button variant="contained" onClick={() => alert("ارسال شد!")}>
              ارسال
            </Button>
          ) : (
            <Button variant="contained" onClick={handleNext}>
              بعدی
            </Button>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default LoanStepper;
